import React from 'react';
import axios from 'axios';

import { Container, Card, Button, Col, Row, Accordion, Form } from 'react-bootstrap';

export default class ConnectForm extends React.Component {
    constructor(props) {
        super(props)
        this.state={title:'', text:'', tag:''}
        this.handleTag = this.handleTag.bind(this);
        this.handleText = this.handleText.bind(this);
        this.handleTitle= this.handleTitle.bind(this);
        this.handleSubmit= this.handleSubmit.bind(this);

    }

    handleTitle = (e) => {
        this.setState({title: e.target.value});
    }
    handleText(e) {
        this.setState({text: e.target.value});
    }
    handleTag(e) {
        this.setState({tag: e.target.value});
    }

    handleSubmit(event) {
        event.preventDefault();

        const userObject = {
            'title': this.state.title,
            'tags': 'Career',
            'text': this.state.text,
            'date': '6-18-20',
            'tag': this.state.tag

        };
        let url ='http://localhost:3001/posts'

        fetch(url, {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json'
            },
            body: json.Stringify(userObject)
          })
            .then((response) => response.json)


    this.setState({ title: '', text: '', tag:'' })


        // axios.post('http://localhost:3001/posts/', userObject)
        //     .then((res) => {
        //         console.log(res.data)
        //     }).catch((error) => {
        //         console.log(error)
        //     });

    }

     render() {
        return(
            <Row className="justify-content-md-center">
            <Button centered variant='outline-secondary' >
            <Accordion>

      <Accordion.Toggle as={Button} variant="link" size='lg' eventKey="0" style={{color:'black'}}>
        Add a Post!
      </Accordion.Toggle>
    <Accordion.Collapse eventKey="0">

      <Form>
            <Form.Group controlId="formBasicEmail">
                <Form.Label>Title</Form.Label>
                <input type="email" placeholder="Enter Title" onChange={this.handleTitle} />

            </Form.Group>

            <Form.Group controlId="formBasicPassword">
                <Form.Label>Text</Form.Label>
                <input type="textarea" placeholder="Enter Text" onChange={this.handleText} />
            </Form.Group>
            {/* <Form.Group controlId="">
            <Form.Label>Tag</Form.Label>

            <Form.Control as="select" onChange={this.hangleTag}>
        <option>Select </option>
      <option>Career</option>
      <option>Education</option>
      <option>Fun</option>

    </Form.Control>
            </Form.Group> */}
            <Button variant="primary" type="submit" onClick={this.handleSubmit}>
                Submit
            </Button>
            </Form>
    </Accordion.Collapse>


</Accordion>
</Button>
</Row>


        );
    }
}
