import React from 'react';
import axios from 'axios';

import { Container, Card, Button, Col, Row, Accordion, Form } from 'react-bootstrap';

export default class ConnectForm extends React.Component {
    constructor(props) {
        super(props)
        this.state={title:'', text:'', tag:'', id: 123}
        this.handleTag = this.handleTag.bind(this);
        this.handleText = this.handleText.bind(this);
        this.handleTitle= this.handleTitle.bind(this);
        this.handleSubmit= this.handleSubmit.bind(this);
        this.makeId= this.makeId.bind(this);


    }

    handleTitle = (e) => {
        this.setState({title: e.target.value});
    }
    handleText(e) {
        this.setState({text: e.target.value});
    }
    handleTag(e) {
        this.setState({tag: e.target.value});
    }

    makeId(length) {
        var result           = '';
        var characters       = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';
        var charactersLength = characters.length;
        for ( var i = 0; i < length; i++ ) {
           result += characters.charAt(Math.floor(Math.random() * charactersLength));
        }
        return result;
     }


    handleSubmit(event) {
        event.preventDefault();

        const userObject = {

            'title': this.state.title,
            'tags': 'Career',
            'text': this.state.text,
            'date': Date.now(),
            'tag': this.state.tag

        };
        // let url ='http://localhost:3001/posts'

    // axios.post(url, { userObject })
    //   .then(res => {
    //     console.log(res);
    //     console.log(res.data);
    //   })

    var d = new Date();


    fetch("http://localhost:3001/posts", {
            method: 'POST',
            headers: {
            'Accept': 'application/json' ,
              'Content-Type': 'application/json'
            },
            body: JSON.stringify( {
                id: this.makeId,
                title: this.state.title,
                tags: "Career",
                text: this.state.text,
                date: d.getMonth() + '-'+ d.getDate() + '-' + d.getFullYear(),
                tag:  this.state.tag

            })
          })
            .then((res) => res.json)
            .then(res => console.log(res))
            .catch(function(res){ console.log(res) })



    this.setState({ title: '', text: '', tag:'' })


        // axios.post('http://localhost:3001/posts/', userObject)
        //     .then((res) => {
        //         console.log(res.data)
        //     }).catch((error) => {
        //         console.log(error)
        //     });
        window.location.reload(false);

    }

     render() {
        return(
            <Row className="justify-content-md-center">
            <Button variant='outline-secondary' >
            <Accordion>

      <Accordion.Toggle as={Button} variant="link" size='lg' eventKey="0" style={{color:'black'}}>
        Add a Post!
      </Accordion.Toggle>
    <Accordion.Collapse eventKey="0">

      <Form>
            <Form.Group controlId="formBasicEmail">
                <Form.Label>Title</Form.Label> <br />
                <input type="email" placeholder="Enter Title" onChange={this.handleTitle} />

            </Form.Group>

            <Form.Group controlId="formBasicPassword">
                <Form.Label>Text</Form.Label>
                <input type="textarea" placeholder="Enter Text" onChange={this.handleText} />
            </Form.Group>
            {/* <Form.Group controlId="">
            <Form.Label>Tag</Form.Label>

            <Form.Control as="select" onChange={this.hangleTag}>
        <option>Select </option>
      <option>Career</option>
      <option>Education</option>
      <option>Fun</option>

    </Form.Control>
            </Form.Group> */}
            <Button variant="primary" type="submit" onClick={this.handleSubmit}>
                Submit
            </Button>
            </Form>
    </Accordion.Collapse>


</Accordion>
</Button>
</Row>


        );
    }
}
